import React from 'react';
import axios from 'axios';
import './Return.css';

class Return extends React.Component {
    
    state = {
        students: [],
        selectedStudent: null,
        issuedBooks: [],
        loading: false
    };

    componentDidMount() {
        this.fetchStudents();
    }

    fetchStudents = () => {
        axios.get('/api/getStudents')
            .then(response => {
                this.setState({ students: response.data });
            })
            .catch(error => {
                console.error('Error fetching students:', error);
            });
    }

    fetchStudentBooks = (studentId) => {
        this.setState({ loading: true });
        axios.get(`/api/getStudent/${studentId}`)
            .then(response => {
                this.setState({ 
                    selectedStudent: response.data.student,
                    issuedBooks: response.data.issuedBooks,
                    loading: false 
                });
            })
            .catch(error => {
                console.error('Error fetching student books:', error);
                this.setState({ loading: false });
                alert('Student not found or error fetching data');
            });
    }

    returnBook = (book) => {
        axios.post('/api/return', {
            id: book.id,
            sid: this.state.selectedStudent.id
        })
        .then(response => {
            alert(`Book "${book.name}" returned successfully`);
            this.fetchStudentBooks(this.state.selectedStudent.id);
        })
        .catch(error => {
            console.error('Error returning book:', error);
            alert('Failed to return book');
        });
    }

    selectStudent = (studentId) => {
        if (studentId) {
            this.fetchStudentBooks(studentId);
        } else {
            this.setState({ selectedStudent: null, issuedBooks: [] });
        }
    }

    render() {
        return (
            <div id='return' className="text-center">
                <h2>Return Books</h2>
                
                <div className="student-selection">
                    <h3>Select Student</h3>
                    <select onChange={(e) => this.selectStudent(e.target.value)}>
                        <option value="">Select a student...</option>
                        {this.state.students.map(student => (
                            <option key={student.id} value={student.id}>
                                {student.name} (ID: {student.id})
                            </option>
                        ))}
                    </select>
                </div>

                {this.state.selectedStudent && (
                    <div className="student-info">
                        <h3>Books Issued to {this.state.selectedStudent.name}</h3>
                        <p>Student ID: {this.state.selectedStudent.id}</p>
                        <p>Current Fine: ${this.state.selectedStudent.fine}</p>
                    </div>
                )}

                {this.state.loading ? (
                    <p>Loading...</p>
                ) : this.state.issuedBooks.length > 0 ? (
                    <table id="rResults" className="table table-hover">
                        <thead id="header">
                            <tr>
                                <th scope="col">Book Name</th>
                                <th scope="col">Author</th>
                                <th scope="col">Issue Date</th>
                                <th scope="col">Return Deadline</th>
                                <th scope="col">Days Overdue</th>
                                <th scope="col">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.issuedBooks.map(book => {
                                const deadline = new Date(book.deadline);
                                const today = new Date();
                                const daysOverdue = Math.max(0, Math.ceil((today - deadline) / (1000 * 60 * 60 * 24)));
                                
                                return (
                                    <tr key={book.id} className={daysOverdue > 0 ? 'overdue' : ''}>
                                        <td>{book.name.toUpperCase()}</td>
                                        <td>{book.author}</td>
                                        <td>{new Date(book.date).toLocaleDateString()}</td>
                                        <td>{deadline.toLocaleDateString()}</td>
                                        <td>{daysOverdue > 0 ? daysOverdue : 'On time'}</td>
                                        <td>
                                            <button 
                                                className="btn btn-primary" 
                                                onClick={() => this.returnBook(book)}
                                            >
                                                Return
                                            </button>
                                        </td>
                                    </tr>
                                );
                            })}
                        </tbody>
                    </table>
                ) : this.state.selectedStudent ? (
                    <p>No books currently issued to this student</p>
                ) : null}
            </div>
        );
    }

}

export default Return;
